import { Component, Input, OnInit } from '@angular/core';
import { Usuario } from '../Usuario';

@Component({
  selector: 'app-viewers-card',
  templateUrl: './viewers-card.component.html',
  styleUrls: ['./viewers-card.component.scss']
})
export class ViewersCardComponent implements OnInit{

  ngOnInit(): void {
  }

  @Input() usuarios : Usuario[];
  hoveredViewer: Usuario | null = null;
  private hideTimeout: any;
  private readonly hideDelay = 5000;

  onThumbnailMouseEnter(usuario: Usuario): void {
    this.clearHideTimeout(); // Limpiar cualquier timeout anterior
    this.hoveredViewer = usuario;
    console.log("thumbnail", usuario)
  }

  onThumbnailMouseLeave(): void {
    this.scheduleHideTooltip();
    console.log("thumbnailLeave", this.usuarios)
  }

  onTooltipMouseEnter(): void {
    this.clearHideTimeout(); // Detener el ocultamiento cuando el mouse entra en la tarjeta
  }

  private scheduleHideTooltip(): void {
    this.clearHideTimeout();
    this.hideTimeout = setTimeout(() => {
      this.hoveredViewer = null;
    }, this.hideDelay);
  }

  onTooltipMouseLeave(): void {
    this.scheduleHideTooltip();  // Programar el ocultamiento cuando el mouse sale de la tarjeta
  }

  private clearHideTimeout(): void {
    if (this.hideTimeout) {
      clearTimeout(this.hideTimeout);
      this.hideTimeout = null;
    }
  }

}
<link rel="stylesheet"
href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.2/css/all.min.css">

<div class="viewers-container">
  <div class="viewers-thumbnail-container">
    <ng-container *ngFor="let usuario of usuarios">
      <div class="viewer-thumbnail-wrapper"
      (mouseenter)="onThumbnailMouseEnter(usuario)"
      (mouseleave)="onThumbnailMouseLeave()">
        <img class="viewer-thumbnail" [src]="usuario.imagenUsuario" alt="{{usuario.nombreUsuario}}">
        <ng-container *ngIf="hoveredViewer === usuario">
          <div class="viewer-card-tooltip-container"
          (mouseenter)="onTooltipMouseEnter()"
          (mouseleave)="onTooltipMouseLeave()">
            <ng-container *ngTemplateOutlet="viewerCardTemplate; context: { $implicit: usuario }">
            </ng-container>
          </div>
        </ng-container>
      </div>
    </ng-container>
  </div>

  <ng-template #viewerCardTemplate let-usuario>
    <div class="viewer-card-tooltip">
      <div class="viewer-card-content">
        <img class="viewer-card-image" [src]="usuario.imagenUsuario" alt="{{usuario.nombreUsuario}}">
        <div class="viewer-card-body">
          <h5 class="viewer-card-title">{{ usuario.nombreUsuario }}</h5>
          <p class="viewer-card-text">{{ usuario.email }}</p>
          <p class="viewer-card-text"><i class="far fa-building"></i>  {{ usuario.cargo }}</p>
        </div>
      </div>
      <hr class="viewer-card-divider">
      <div class="viewer-card-footer">
        <button class="detail-viewer-button">
          <i class="fa-regular fa-envelope"></i>
        </button>
        <button class="detail-viewer-button">
          <i class="fa-regular fa-calendar"></i>
        </button>
        <button class="detail-viewer-button">
          <i class="fa-regular fa-comment"></i>
        </button>
      </div>
    </div>
  </ng-template>

</div>
