import { Component, Input } from '@angular/core';
import { Usuario } from '../Usuario';

@Component({
  selector: 'app-viewers-card',
  templateUrl: './viewers-card.component.html',
  styleUrls: ['./viewers-card.component.scss']
})
export class ViewersCardComponent {
  @Input() usuarios : Usuario[];

  hoveredViewer: Usuario | null = null;
  keepOpen: boolean = false;

  showViewerCard(usuario: Usuario): void {
    setTimeout(() => {
      this.hoveredViewer = usuario;
    }, 1000);
  }

  hideViewerCard(): void {
    if (!this.keepOpen){
      this.hoveredViewer = null;
    }
  }

  onViewerCardMouseEnter(): void {
    this.keepOpen = true;
  }

  onViewerCardMouseLeave(): void {
    this.keepOpen = false;
    this.hoveredViewer = null;
  }
}
<div class="viewers-container">
  <div *ngFor="let usuario of usuarios">
    <!-- <div (mouseenter)="showViewerCard(usuario)" (mouseleave)="hideViewerCard()"> -->
      <img class="viewer-thumbnail" [src]="usuario.imagenUsuario" alt="{{usuario.nombreUsuario}}">
      <!-- <div   (mouseenter)="onViewerCardMouseEnter()" (mouseleave)="onViewerCardMouseLeave()"> -->
        <div class="viewer-card-container">
          <div class="viewer-card" *ngIf="hoveredViewer != usuario">
            <img class="viewer-card-image" [src]="usuario.imagenUsuario" alt="{{usuario.nombreUsuario}}">
            <div class="viewer-card-body">
              <h5 class="viewer-card-title">{{ usuario.nombreUsuario }}</h5>
              <p class="viewer-card-text">Email: {{ usuario.email }}</p>
              <p class="viewer-card-text">Cargo: {{ usuario.cargo }}</p>
            </div>
            <hr class="viewer-card-divider">
            <div class="viewer-card-footer">
              <button class="detail-viewer-button">Mas Informacion</button>
            </div>
          </div>
        </div>
      <!-- </div> -->
    <!-- </div> -->
  </div>
</div>


