import { Injectable, isDevMode } from '@angular/core';
import * as signalR from '@aspnet/signalr';
import { ExpectedMessage } from './expected.messages';


@Injectable({
  providedIn: 'root'
})
export class SignalRService {
  private hubConnection: signalR.HubConnection;
  private connectionName: string = "alice"; // Puedes cambiar esto o hacerlo configurable
  private connectionId: string = "";

  public startConnection = () => {
    this.hubConnection = new signalR.HubConnectionBuilder()
      .withUrl(`https://localhost:44385/SignalHub?connectionName=${this.connectionName}`)
      .build();

    this.hubConnection.start()
      .then(() => console.log("Connection started"))
      .catch(err => console.error("Error while starting connection: " + err));



    this.hubConnection.on(ExpectedMessage.welcome, (connectionId) => {
      console.log(`Welcome: ${connectionId}`);
      this.connectionId = connectionId;

      c


    });


    this.hubConnection.on('ReceivePayloadResponse', (data) => {
      isDevMode() && console.log({
        data: JSON.stringify(data)
      })
    });

    this.hubConnection.on(ExpectedMessage.show_notification, () => {
      this.showNotification();
    });
  }

  public sendPayload(payload: any) {
    this.hubConnection.invoke('ReceivePayload', this.connectionId, payload)
      .catch(err => console.error(err));
  }

  private showNotification() {
    var notification = new Notification(this.connectionName, {
      body: 'You clicked on the button!',
      icon: 'https://i.pinimg.com/originals/35/0e/dd/350edd537688ad50ea3c5615e02ba84e.jpg'
    });
    setTimeout(function () { notification.close(); }, 5000);
  }
}
