import { Component, Input, OnInit, AfterViewInit, ViewChild, ElementRef } from '@angular/core';
import { Usuario } from '../Usuario';
// import { StyleAdjusterService } from '../services/style-adjuster.service';

@Component({
  selector: 'app-viewers-card',
  templateUrl: './viewers-card.component.html',
  styleUrls: ['./viewers-card.component.scss']
})
export class ViewersCardComponent implements OnInit, AfterViewInit{
  @ViewChild('viewerCardContainer', { static: false }) viewerCardContainer: ElementRef;

  ngOnInit(): void {
  }

  @Input() usuarios : Usuario[];
  hoveredViewer: Usuario | null = null;
  private hideTimeout: any;
  private showTimeout: any;
  private readonly hideDelay = 700;
  private readonly showDelay = 300;
  private visibleUserCount = 10; // Número máximo de usuarios visibles inicialmente
//  private showAll = false; // Flag para mostrar todos los usuarios
  isDropdownOpen = false; //Controla la visibilidad del dropdown

  // private styleAdjuster: StyleAdjusterService
  constructor() {}

  ngAfterViewInit(){
    if(this.viewerCardContainer){
      // this.styleAdjuster.adjustStyles(this.viewerCardContainer.nativeElement)
    }
  }

  onMouseEnter(usuario: Usuario, isTooltip: boolean = false): void {
    this.clearHideTimeout();
    this.clearShowTimeout();
    this.showTimeout = setTimeout(() => {
      this.hoveredViewer = usuario;
    }, this.showDelay);
  }

  onMouseLeave(): void {
    this.clearShowTimeout();
    this.scheduleHideTooltip();
  }

  private scheduleHideTooltip(): void {
    this.clearHideTimeout();
    this.hideTimeout = setTimeout(() => {
      this.hoveredViewer = null;
    }, this.hideDelay);
  }

  private clearHideTimeout(): void {
    if (this.hideTimeout) {
      clearTimeout(this.hideTimeout);
      this.hideTimeout = null;
    }
  }

    private clearShowTimeout(): void {
    if (this.showTimeout) {
      clearTimeout(this.showTimeout);
      this.showTimeout = null;
    }
  }

  // Método para alternar la visibilidad de los usuarios
  toggleUsers() {
    // this.showAll = !this.showAll;
    // this.visibleUserCount = this.showAll ? this.usuarios.length : 10;
    this.isDropdownOpen = !this.isDropdownOpen; //Alterna el estado del dropdown
    console.log(this.isDropdownOpen)
  }

  // Obtener usuarios restantes
  get remainingUsers() {
    return this.usuarios.slice(this.visibleUserCount);
  }

  // Obtener usuarios visibles en función del estado
  get visibleUsers() {
    return this.usuarios.slice(0, this.visibleUserCount);
  }

  // Determinar si se debe mostrar el icono de "más"
  get showMoreIcon() {
    // return !this.showAll && this.usuarios.length > 10;
    return this.usuarios.length > this.visibleUserCount;
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <!-- <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.2/css/all.min.css"> -->
</head>
<body>
  <div class="viewers-container" #viewersContainer>
    <ng-container *ngFor="let usuario of visibleUsers">
      <div class="viewer-thumbnail-wrapper" (mouseenter)="!isDropdownOpen && onMouseEnter(usuario)" (mouseleave)="!isDropdownOpen && onMouseLeave()">
        <div class="viewer-thumbnail-image">
          <img class="viewer-thumbnail-img" [src]="usuario.imagenUsuario" [alt]="usuario.nombreUsuario">
          <ng-container *ngIf="hoveredViewer === usuario  && !isDropdownOpen">
            <div class="viewer-card-tooltip-container" appTooltipAdjust [appTooltipAdjust]="true" #viewerCardContainer (mouseenter)="onMouseEnter(usuario, true)" (mouseleave)="onMouseLeave()">
              <ng-container *ngTemplateOutlet="viewerCardTemplate; context: { $implicit: usuario }"></ng-container>
            </div>
          </ng-container>
        </div>
      </div>
    </ng-container>

     <!-- Contenedor relativo para el botón y la lista desplegable -->
    <div class="viewers-container-more" *ngIf="showMoreIcon">
      <button class="detail-more-button" (click)="toggleUsers()">
        <i [ngClass]="isDropdownOpen ? 'fas fa-chevron-circle-up' : 'fa-solid fa-circle-plus'"></i>
      </button>

      <!-- Dropdown de usuarios restantes -->
      <div *ngIf="isDropdownOpen" class="dropdown-menu">
        <ng-container *ngFor="let usuario of remainingUsers">
          <div class="dropdown-item">
            <div class="dropdown-item-content">
              <div class="viewer-item-image">
                <img class="viewer-item-img" [src]="usuario.imagenUsuario" [alt]="usuario.nombreUsuario">
                <span class="viewer-card-status"></span>
              </div>
              <div class="dropdown-item-body">
                <h2 class="viewer-item-nameuser">{{ usuario.nombreUsuario }}</h2>
                <h3 class="viewer-item-email">{{ usuario.email }}</h3>
                <h3 class="viewer-item-positionjob"><i class="far fa-building"></i> {{ usuario.cargo }}</h3>
              </div>
            </div>
            <div class="dropdown-item-footer">
              <button class="detail-viewer-button"><i class="fa-regular fa-envelope"></i></button>
              <button class="detail-viewer-button"><i class="fa-regular fa-calendar"></i></button>
              <button class="detail-viewer-button"><i class="fa-regular fa-comment"></i></button>
            </div>
          </div>
        </ng-container>
      </div>
    </div>

    <ng-template #viewerCardTemplate let-usuario>
      <div class="viewer-card-tooltip">
        <div class="viewer-card-content">
          <div class="viewer-card-image">
            <img class="viewer-card-img" [src]="usuario.imagenUsuario" [alt]="usuario.nombreUsuario">
            <span class="viewer-card-status"></span>
          </div>
          <div class="viewer-card-body">
            <h2 class="viewer-card-nameuser">{{ usuario.nombreUsuario }}</h2>
            <h3 class="viewer-card-email">{{ usuario.email }}</h3>
            <h3 class="viewer-card-positionjob"><i class="far fa-building"></i> {{ usuario.cargo }}</h3>
          </div>
        </div>
        <div class="viewer-card-footer">
          <button class="detail-viewer-button"><i class="fa-regular fa-envelope"></i></button>
          <button class="detail-viewer-button"><i class="fa-regular fa-calendar"></i></button>
          <button class="detail-viewer-button"><i class="fa-regular fa-comment"></i></button>
        </div>
      </div>
    </ng-template>
  </div>
</body>
</html>
