import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-viewers-card',
  templateUrl: './viewers-card.component.html',
  styleUrls: ['./viewers-card.component.scss']
})
export class ViewersCardComponent {
  @Input() nombreUsuario: string;
  @Input() idUsuario: number;
  @Input() email: string;
  @Input() cargo: string;
  @Input() imagenUsuario: string;

}
<div class="viewer-card">
  <img class="viewer-card-image" [src]="imagenUsuario" alt="{{nombreUsuario}}">
  <div class="viewer-card-body">
    <h5 class="viewer-card-title">{{ nombreUsuario }}</h5>
    <!-- <p class="card-text">ID: {{ idUsuario }}</p> -->
    <p class="viewer-card-text">Email: {{ email }}</p>
    <p class="viewer-card-text">Cargo: {{ cargo }}</p>
  </div>
  <div class="viewer-card-footer">
    <button class="detail-viewer-button">Mas Informacion</button>
  </div>
</div>
