import { Directive, ElementRef, Renderer2, HostListener, Input } from '@angular/core';

@Directive({
  selector: '[appTooltipAdjust]'
})
export class TooltipAdjustDirective {
  @Input() appTooltipAdjust: boolean;

  constructor(private element: ElementRef, private renderer: Renderer2) { }

  @HostListener('window:resize')
  onWindowResize() {
    if (this.appTooltipAdjust) {
      this.adjustTooltipPosition();
    }
  }

  private adjustTooltipPosition() {
    if (!this.appTooltipAdjust) return;

    const element = this.element.nativeElement;
    const rect = element.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;

    // Ajustar la posición si la tarjeta se sale del margen derecho
    if (rect.right > viewportWidth) {
      this.renderer.setStyle(element, 'left', `-${rect.right - viewportWidth}px`);
    }

    // Ajustar la posición si la tarjeta se sale del margen izquierdo
    if (rect.left < 0) {
      this.renderer.setStyle(element, 'left', `${Math.abs(rect.left)}px`);
    }

    // Ajustar la posición si la tarjeta se sale del margen superior
    if (rect.top < 0) {
      this.renderer.setStyle(element, 'top', `${Math.abs(rect.top)}px`);
    }

    // Ajustar la posición si la tarjeta se sale del margen inferior
    if (rect.bottom > viewportHeight) {
      this.renderer.setStyle(element, 'top', `${viewportHeight - rect.bottom}px`);
    }
  }
}
